import { Component } from '@angular/core';
import { Book } from 'src/app/entities/Book';

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent {
  title:String="";
 author:String="";
 bookcondition:number=0;

 constructor()

 createBook():Book{
  const createdBook=new Book(0,this.title,this.author,this.bookcondition,true,0);
  return createdBook;
}



  ngOnInit(): void {
  }
}

<p>create-book works!</p>
<label  for="title">Title</label>
<input id="title" type="text">

<label  for="author">Author</label>
<input id="author" type="text">

<label  for="bookcondition">Condition</label>
<input id="bookcondition" type="number">

<button onclick="createBook()"></button>
